import '../css/common.css';

const refs = {
  startBtn: document.querySelector('button[data-action-start]'),
  stopBtn: document.querySelector('button[data-action-stop]'),
  clockface: document.querySelector('.js-clockface'),
};

class Timer {
  constructor({ onTick }) {
    this.intervalId = null;
    this.isActive = false;
    this.onTick = onTick;

    this.init();
  }

  init() {
    const time = this.getTimeComponents(0);
    this.onTick(time);
  }

  start() {
    if (this.isActive) {
      return;
    }
    const startTime = Date.now();
    this.isActive = true;

    this.intervalId = setInterval(() => {
      const currentTime = Date.now();
      const deltaTime = currentTime - startTime;
      const time = this.getTimeComponents(deltaTime);
      this.onTick(time);
    }, 1000);
  }

  stop() {
    clearInterval(this.intervalId);
    this.isActive = false;
    const time = this.getTimeComponents(0);
    this.onTick(time);
  }

  /*
   * - ĞŸÑ€Ğ¸Ğ½Ğ¸Ğ¼Ğ°ĞµÑ‚ Ğ²Ñ€ĞµĞ¼Ñ Ğ² Ğ¼Ğ¸Ğ»Ğ»Ğ¸ÑĞµĞºÑƒĞ½Ğ´Ğ°Ñ…
   * - Ğ’Ñ‹ÑÑ‡Ğ¸Ñ‚Ñ‹Ğ²Ğ°ĞµÑ‚ ÑĞºĞ¾Ğ»ÑŒĞºĞ¾ Ğ² Ğ½Ğ¸Ñ… Ğ²Ğ¼ĞµÑ‰Ğ°ĞµÑ‚ÑÑ Ñ‡Ğ°ÑĞ¾Ğ²/Ğ¼Ğ¸Ğ½ÑƒÑ‚/ÑĞµĞºÑƒĞ½Ğ´
   * - Ğ’Ğ¾Ğ·Ğ²Ñ€Ğ°Ñ‰Ğ°ĞµÑ‚ Ğ¾Ğ±ÑŒĞµĞºÑ‚ ÑĞ¾ ÑĞ²Ğ¾Ğ¹ÑÑ‚Ğ²Ğ°Ğ¼Ğ¸ hours, mins, secs
   * - ĞĞ´ÑĞºĞ°Ñ ĞºĞ¾Ğ¿Ğ¸Ğ¿Ğ°ÑÑ‚Ğ° ÑĞ¾ ÑÑ‚ĞµĞºĞ° ğŸ’©
   */
  getTimeComponents(time) {
    const hours = this.pad(
      Math.floor((time % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)),
    );

    const mins = this.pad(Math.floor((time % (1000 * 60 * 60)) / (1000 * 60)));
    const secs = this.pad(Math.floor((time % (1000 * 60)) / 1000));

    return { hours, mins, secs };
  }

  /*
   * ĞŸÑ€Ğ¸Ğ½Ğ¸Ğ¼Ğ°ĞµÑ‚ Ñ‡Ğ¸ÑĞ»Ğ¾, Ğ¿Ñ€Ğ¸Ğ²Ğ¾Ğ´Ğ¸Ñ‚ Ğº ÑÑ‚Ñ€Ğ¾ĞºĞµ Ğ¸ Ğ´Ğ¾Ğ±Ğ°Ğ²Ğ»ÑĞµÑ‚ Ğ² Ğ½Ğ°Ñ‡Ğ°Ğ»Ğ¾ 0 ĞµÑĞ»Ğ¸ Ñ‡Ğ¸ÑĞ»Ğ¾ Ğ¼ĞµĞ½ÑŒÑˆĞµ 2-Ñ… Ğ·Ğ½Ğ°ĞºĞ¾Ğ²
   */
  pad(value) {
    return String(value).padStart(2, '0');

    // 1 -> 01
    // 7 -> 07
    // 12 -> 12
  }
}

const timer = new Timer({
  onTick: updateClockface,
});

refs.startBtn.addEventListener('click', timer.start.bind(timer));

refs.stopBtn.addEventListener('click', () => {
  timer.stop();
});
/*
 * - ĞŸÑ€Ğ¸Ğ½Ğ¸Ğ¼Ğ°ĞµÑ‚ Ğ²Ñ€ĞµĞ¼Ñ Ğ² Ğ¼Ğ¸Ğ»Ğ»Ğ¸ÑĞµĞºÑƒĞ½Ğ´Ğ°Ñ…
 * - Ğ’Ñ‹ÑÑ‡Ğ¸Ñ‚Ñ‹Ğ²Ğ°ĞµÑ‚ ÑĞºĞ¾Ğ»ÑŒĞºĞ¾ Ğ² Ğ½Ğ¸Ñ… Ğ²Ğ¼ĞµÑ‰Ğ°ĞµÑ‚ÑÑ Ñ‡Ğ°ÑĞ¾Ğ²/Ğ¼Ğ¸Ğ½ÑƒÑ‚/ÑĞµĞºÑƒĞ½Ğ´
 * - Ğ Ğ¸ÑÑƒĞµÑ‚ Ğ¸Ğ½Ñ‚ĞµÑ€Ñ„ĞµĞ¹Ñ
 */
function updateClockface({ hours, mins, secs }) {
  refs.clockface.textContent = `${hours}:${mins}:${secs}`;
}
